import React, { useState } from "react";
import "./Dropdown.css";

const Dropdown = (props) => {
  const [isActive, setIsActive] = useState(false);

  const options = ["Vortex", "Five Jets"];

  const onBtnClickHandler = () => {
    setIsActive(!isActive);
  }

  const onItemClickHandler = e => {
    props.setSelectedDataset(e.target.textContent)
    setIsActive(!isActive);
  }

  return (
    <div className="dropdown">
      <div className="dropdown-btn" onClick={onBtnClickHandler}>{props.selectedDataset}</div>
      {isActive && (
        <div className={(isActive) ? ("show") : ("")}>
            {options.map(option=>(<div className="dropdown-item" onClick={onItemClickHandler}>{option}</div>))}
        </div>
      )}
    </div>
  );
};

export default Dropdown;
