import React, { useState } from "react";
import ImageSection from "./ImageSection/ImageSection";
import SideBar from "./SideBar/SideBar";

import "./AppBody.css";

const AppBody = () => {
    const DSET_LIMITS = {
        theta: [0, 171, 9],
        phi: [0, 351, 9],
        time_step: {
            vortex: [3, 90, 3],
            ionization: [2, 100, 2],
            tornado: [1, 48, 1],
            fivejets: [1991, 1991, 0],
            tangaroa: [196, 196, 0]
        }
    }

    const INIT_PARAMS = {
        dset: "Vortex",
        render: "IR",
        time_step: "3",
        theta: "45",
        phi: "90",
    };

    const [params, setParams] = useState(INIT_PARAMS);

    const paramsChangeHandler = (selectedParams) => {
        setParams(selectedParams);
    };

    return (
        <div className="app-body-container">
            <div id="image-sec-div">
                <ImageSection imageParams={params} />
            </div>
            <div id="sidebar-div">
                <SideBar
                    limits={DSET_LIMITS}
                    initialValues={INIT_PARAMS}
                    onParamsChange={paramsChangeHandler}
                />
            </div>
        </div>
    );
};

export default AppBody;
