import React, { useState } from "react";
import "./Dropdown.css";

const Dropdown = (props) => {
  const [isActive, setIsActive] = useState(false);
  const onBtnClickHandler = () => {
    setIsActive(!isActive);
  }

  const onItemClickHandler = e => {
    props.setSelectedDataset(e.target.textContent)
    setIsActive(!isActive);
  }

  return (
    <div className="dropdown">
      <div className="dropdown-btn" onClick={onBtnClickHandler}>{props.selectedDataset}</div>
      {isActive && (
        <div className="dropdown-content">
          <div className="dropdown-item" onClick={onItemClickHandler}>Vortex</div>
          <div className="dropdown-item" onClick={onItemClickHandler}>Five Jets</div>
        </div>
      )}
    </div>
  );
};

export default Dropdown;
