import React, {useState} from "react";
import FilterSlider from "./FilterSlider";

import "./SideBar.css";

const SideBar = () => {
  const [selectedTimeStep, setSelectedTimeStep] = useState("")
  const [selectedIsoValue, setSelectedIsoValue] = useState("")
  const [selectedTheta, setSelectedTheta] = useState("")
  const [selectedPhi, setSelectedPhi] = useState("")

  const timeStepChangeHandler = e =>{
    setSelectedTimeStep(e.target.value);
  }

  const IsoValueChangeHandler = e =>{
    setSelectedIsoValue(e,target.value);
  }

  const ThetaChangeHandler = e =>{
    setSelectedTheta(e.target.value);
  }

  const PhiChangeHandler = e =>{
    setSelectedPhi(e,target.value);
  }

  const formChangeHandler = (e) => {
    console.log("Timestep: " + selectedTimeStep);
  }

  return (
    <div className="sidebar-wrapper">
      <div className="divider">
        <label id="dataset-label">Dataset</label>
        <select>
          <option value="vortex">Vortex</option>
        </select>
      </div>
      <div className="divider">
        <form>
          <FilterSlider title="Time Step" min="3" max="90" step="3" onChangeHandler={timeStepChangeHandler}/>
          <FilterSlider title="Iso Value" min="-0.4" max="0.4" step="0.1" onChangeHandler={IsoValueChangeHandler}/>
          <FilterSlider title="Theta" min="45" max="135" step="15" onChangeHandler={ThetaChangeHandler}/>
          <FilterSlider title="Phi" min="90" max="270" step="15" onChangeHandler={PhiChangeHandler}/>
        </form>
      </div>
    </div>
  );
};

export default SideBar;
