import React from "react";
import FilterSlider from "./FilterSlider";

import "./SideBar.css";

const SideBar = () => {
  const [selectedTimeStep, setSelectedTimeStep] = useState("");

  const formChangeHandler = (e) => {

  }

  return (
    <div className="sidebar-wrapper">
      <div className="divider">
        <label id="dataset-label">Dataset</label>
        <select>
          <option value="vortex">Vortex</option>
        </select>
      </div>
      <div className="divider">
        <form>
          <FilterSlider title="Time Step" min="3" max="90" step="3" value={selectedTimeStep} onChangeHandler={formChangeHandler}/>
          <FilterSlider title="Iso Value" min="-0.4" max="0.4" step="0.1" onChangeHandler={formChangeHandler}/>
          <FilterSlider title="Theta" min="45" max="135" step="15" onChangeHandler={formChangeHandler}/>
          <FilterSlider title="Phi" min="90" max="270" step="15" onChangeHandler={formChangeHandler}/>
        </form>
      </div>
    </div>
  );
};

export default SideBar;
